import { useDispatch, useSelector } from "react-redux";
import classes from "./Counter.module.css";

const Counter = () => {
  const storeCounter = useSelector((state) => state.counter);
  const dispatch = useDispatch();
  // useSelector hook is to access store state & get the store state,it accepts function as a 1st param,it gives the initial state snapshot from the store, it accepts second param also a function - equality function whether the component need to be re-render or not, soto say its check the state, if updated react trigger component to re-render, if not nothing happens...
  const increCount = () => {
    dispatch({ type: "increment" });
  };
  const decreCount = () => {
    dispatch({ type: "decrement" });
  };
  const toggleCounterHandler = () => {};

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{storeCounter}</div>
      <div>
        <button onClick={increCount}>Increment</button>
        <button onClick={decreCount}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
